var WJ={Widget:{GetJobs:function(t){var e="http://localhost:8000",a=t;jQuery.ajax({url:e+"/api/company/"+a,dataType:"json",crossDomain:!0,success:function(t){if(t<=0)jQuery("#wj-app").html('<div class="jumbotron position-relative text-center"><span class="display-4">Sorry there are no current openings</span><p class="lead mt-5">Check back soon!</p><small class="position-absolute" style="right:3%;bottom:7%">Powered by <a href="'+e+'" target="_blank">WhiteJuly.com</a></small></div>');else{jQuery("#wj-app").html('<table id="wj-table" class="table table-hover position-relative"><tr><th scope="col">Position</th><th scope="col">Department</th><th scope="col">Location</th><th scope="col"></th></tr>');for(var o=0;o<t.length;o++){var s=t[o],r="<tr><td><a href='"+e+"/postings/view/"+a+"/"+s.id+"' target='_blank'>"+s.title+"</a></td><td>"+s.department+"</td><td>"+s.location+"</td><td><a href='"+e+"/postings/view/"+a+"/"+s.id+"' class='btn btn-success btn-sm' target='_blank'>View Job</a></td></tr>";jQuery("#wj-table").find("tbody").append(r)}jQuery("#wj-app").append("<div class='mt-5 text-right'><small>Powered by <a href='"+e+"' target='_blank'>WhiteJuly.com</a></small></div>")}},error:function(){console.log("There was an error loading the data from White July")}})}}};jQuery(document).ready(function(){var t=document.getElementById("wj-app").className.replace("wj-","");WJ.Widget.GetJobs(t)});