var WJ={Init:function(){},Widget:{GetJobs:function(t){var o=t;console.log(o),$.ajax({url:"http://localhost:8000/api/company/"+o,dataType:"json",crossDomain:!0,success:function(t){$("#wj-app").html('<table id="wj-table" class="table table-hover"><tr><th scope="col">Position</th><th scope="col">Department</th><th scope="col">Location</th><th scope="col"></th></tr>');for(var e=0;e<t.length;e++){var a=t[e],n="<tr><td>"+a.title+"</td><td>"+a.department+"</td><td>"+a.location+"</td><td><a href='http://localhost:8000/postings/view/"+o+"/"+a.id+"' class='btn btn-success btn-sm' target='_blank'>View Job</a></td></tr>";$("#wj-table").find("tbody").append(n)}},error:function(){console.log("There was an error loading the data from White July")}})}}};$(document).ready(function(){var t=document.getElementById("wj-app").className.replace("wj-","");WJ.Widget.GetJobs(t)});